import React, { useState, useEffect } from 'react';
import { Button,SafeArea, Image,Text, View, Platform, SafeAreaView } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import Constants from 'expo-constants';

export default function ImagePickerExample() {
  const [image, setImage] = useState(null);
  const [responseData, setResponseData] = useState(null);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: 'Images',
      allowsEditing: false,
      quality: 1,
    });

    
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };


  const pickCamera = async () => {
    //ImagePicker.launchCameraAsync(options)








    let result = await ImagePicker.launchCameraAsync({
      mediaTypes: 'Images',
      allowsEditing: false,
      quality: 1,
    });

    if (!result.cancelled) {
      {
        setImage(result.uri);
        uploadImage(result)
      }
    }
  };

  const uploadImage = async (result) => {


    let localUri = result.uri;
    let filename = localUri.split('/').pop();
  
    let match = /\.(\w+)$/.exec(filename);
    let type = match ? `image/${match[1]}` : `image`;
  
    console.log(result)
    let data = {
      uri: localUri,
      name: filename,
      type: type
    }


   await fetch('http://localhost/ObjectDetection/VisionAPI/index.php', {
      method: 'POST',
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
  }) .then(response => response.json())
  .then(data =>  setResponseData(data));
  };


  return (
    
    <View>
      <Button title="Pick an image from camera roll" onPress={pickImage} />
      <Button title="Pick an image from camera roll" onPress={pickCamera} />
      {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
    </View>
    
  );
}
